mapper.select:
select id, coupon_code, coupon_name, category, coupon_type, start_date, end_date, status, to_char(created_at,'YYYY-MM-DD') as createdAt from coupon_master
where 1 = 1

    and start_date >= #{startDate}


    and end_date <= #{endDate}


    and category = #{category}


    and status = #{status}

order by created_at desc
limit #{pageSize} offset #{offset}

select count(*) from coupon_master
where 1 = 1

    and start_date >= #{startDate}


    and end_date <= #{endDate}


    and category = #{category}


    and status = #{status}

mapper.insert:
insert into coupon_master ( category, coupon_code, coupon_name, coupon_type, discount_value, max_discount_amount, min_order_amount, use_limit_days, start_date, end_date, status )values( #{category},
#{couponCode}
concat('CPN',date_format(current_timestamp, '%Y%m%d'),lpad((select coalesce(max(id), 0) + 1 from coupon_master), 4, '0'))
, #{couponName}, #{couponType}, #{discountValue}, #{maxDiscountAmount}, #{minOrderAmount}, #{useLimitDays}, #{startDate}, #{endDate}, #{status} ) on duplicate key update category = #{category}, coupon_name = #{couponName}, coupon_type = #{couponType}, discount_value = #{discountValue}, max_discount_amount = #{maxDiscountAmount}, min_order_amount = #{minOrderAmount}, use_limit_days = #{useLimitDays}, start_date = #{startDate}, end_date = #{endDate}, status = #{status}
